
import { useState, useEffect } from 'react';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [contacts, setContacts] = useState([
    { name: 'John', avatar: 'data:image/jpeg;base64,' },
    { name: 'Jane', avatar: 'data:image/jpeg;base64,' },
  ]); 

  const handleSendMessage = () => {
    if (inputMessage.trim() !== '') {
      setMessages([...messages, { text: inputMessage, sender: 'user' }]);

      setTimeout(() => {
        setMessages([...messages, { text: 'ตอบกลับจากระบบ', sender: 'system' }]);
      }, 1000);
      setInputMessage('');
    }
  };


  useEffect(() => {
    const intervalId = setInterval(() => {
      setMessages([...messages, { text: 'ตอบกลับจากระบบ', sender: 'system' }]);
    }, 3000);

    return () => clearInterval(intervalId);
  }, [messages]);

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
     
      <div style={{ width: '200px', borderRight: '1px solid #ccc', padding: '20px' }}>
        <h2 style={{ marginBottom: '20px' }}>Contacts</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {contacts.map((contact, index) => (
            <li key={index} style={{ marginBottom: '10px', cursor: 'pointer', display: 'flex', alignItems: 'center' }}>
              <img
                src={contact.avatar}
                alt={`${contact.name}'s avatar`}
                style={{ width: '50px', height: '50px', borderRadius: '50%', marginRight: '10px' }}
              />
              <span>{contact.name}</span>
            </li>
          ))}
        </ul>
      </div>

      <div style={{ flex: 1, padding: '20px', overflowY: 'auto' }}>
        <div>
          {messages.map((message, index) => (
            <div key={index} className={message.sender} style={{ display: 'flex', marginBottom: '10px' }}>
              {message.sender === 'system' ? (
                <div style={{ width: '50px', height: '50px', borderRadius: '50%', marginRight: '10px', backgroundColor: '#ccc', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  🤖
                </div>
              ) : (
                <img
                  src={contacts.find(contact => contact.name === message.sender)?.avatar}
                  alt={`${message.sender}'s avatar`}
                  style={{ width: '50px', height: '50px', borderRadius: '50%', marginRight: '10px' }}
                />
              )}
              <div style={{ padding: '10px', backgroundColor: message.sender === 'system' ? '#f0f0f0' : '#87CEEB', borderRadius: '10px' }}>
                {message.text}
              </div>
            </div>
          ))}
        </div>
        <div style={{ marginTop: '20px' }}>
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
          />
          <button onClick={handleSendMessage}>ส่ง</button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
